<script>
    $effect(() => {
        const input = document.getElementById("input-slider");
        const returnValueSpan = document.getElementById("return-value");
        const thumb = document.getElementById("thumb");
        const progressTrack = document.getElementById("progress-track");
        const tooltip = document.getElementById("tooltip");
        const tooltipValue = document.getElementById("tooltip-value");
        input?.addEventListener("input", (e) => {
            if (e.currentTarget?.value) {
                const value = Math.round(e.currentTarget.value);
                const percentage = ((value - 3000) * 100) / (100_000 - 3000);
                console.log(percentage);
                if (returnValueSpan) {
                    returnValueSpan.innerHTML = `${Math.round(value * 0.3)}`;
                }
                if (thumb) {
                    thumb.style.insetInlineStart = `${percentage}%`;
                }
                if (progressTrack) {
                    progressTrack.style.width = `${percentage}%`;
                }
                if (tooltip) {
                    tooltip.style.insetInlineStart = `${percentage}%`;
                }
                if (tooltipValue) {
                    tooltipValue.innerHTML = `${value}`;
                }
            }
        });
    });
</script>

